import { useState, useEffect } from 'react';
import './App.css';
import axios from 'axios';
import CurrencyConverter from './CurrencyConverter';

function App() {
  const [amount1, setAmount1] = useState(1);
  const [currency1, setCurrency1] = useState('USD');
  const [amount2, setAmount2] = useState(1);
  const [currency2, setCurrency2] = useState('USD');
  const [rates, setRates] = useState([]);

  const handleAmountChange = (amount1) => {
    setAmount2((amount1 * rates[currency2]) / rates[currency1]);
    setAmount1(amount1);
  };

  const handleCurrencyChange = (currency1) => {
    setAmount2((amount1 * rates[currency2]) / rates[currency1]);
    setCurrency1(currency1);
  };

  useEffect(() => {
    axios
      .get(
        'https://api.apilayer.com/fixer/latest?base=USD&apikey=YRbC1AhQTGH24CyznlrIbRrNwYEzN1Z1'
      )
      .then((response) => {
        setRates(response.data.rates);
      })
      .catch((error) => {
        console.log('We got a problem!', error);
      });
  }, []);

  return (
    <div className='App'>
      <CurrencyConverter
        onAmountChange={handleAmountChange}
        onCurrencyChange={handleCurrencyChange}
        currencies={Object.keys(rates)}
        currency={currency1}
        amount={amount1}
      />
      <CurrencyConverter
        onAmountChange={setAmount2}
        onCurrencyChange={setCurrency2}
        currencies={Object.keys(rates)}
        currency={currency2}
        amount={amount2}
      />
    </div>
  );
}

export default App;
